DROP TABLE IF EXISTS `section`;DROP TABLE IF EXISTS `content`;CREATE TABLE `content` (  `id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,  `body` varchar(255) NULL,  PRIMARY KEY (`id`));CREATE TABLE `section` (`id` bigint(20) UNSIGNED NOT NULL AUTO_INCREMENT,`name` varchar(255) NULL,`published` boolean NOT NULL,`content_id` bigint(20) UNSIGNED NOT NULL,`parent_id` bigint(20) UNSIGNED,PRIMARY KEY (`id`),INDEX `FK_DEPT` (`parent_id`),CONSTRAINT `FK_DEPT` FOREIGN KEY (`parent_id`) REFERENCES `section` (`id`),CONSTRAINT `content_id` FOREIGN KEY (`content_id`) REFERENCES `content` (`id`));